import React from "react";
// reactstrap components
import {
  Container,
  Row,
} from "reactstrap";

import axios from 'axios';

// core components
import Navbar from "components/Navbars/Navbar.jsx";
import Product from "components/Product.jsx";

import { addCartItem } from '../actions/index'

class Products extends React.Component {
  state = {
    squares1to6: "",
    squares7and8: "",
    products: []
  };

  async componentWillMount () {
    this.setState({ products: await this.fetchProducts() })
  }

  componentDidMount () {
    document.body.classList.toggle("register-page");
  }

  componentWillUnmount () {
    document.body.classList.toggle("register-page");
  }

  async fetchProducts () {
    try {
      const products = await axios.get('http://localhost:3001/products')
      return products.data
    } catch (e) {
      console.log(e.message)
    }
    return []
  }

  addToCart = (index) => {
    window.store.dispatch( addCartItem(this.state.products[index]) )
    this.setState({ products: this.state.products.filter((product, idx) => idx !== index) })
  }

  render() {
    return (
      <>
        <Navbar />
        <div className="wrapper">
          <div className="page-header">
            <div className="page-header-image" />
            <div className="content">
              <Container>
                <Row style={{zIndex: 9000}}>
                  { this.state.products.map(
                      (product, idx) => (<Product index={ idx } addToCart={ this.addToCart } key={ product.id } name={ product.name } price={ product.price } />)
                    )
                  }
                </Row>
                <div
                  className="square square-3"
                  id="square3"
                  style={{ transform: this.state.squares1to6 }}
                />
                <div
                  className="square square-4"
                  id="square4"
                  style={{ transform: this.state.squares1to6 }}
                />
                <div
                  className="square square-6"
                  id="square6"
                  style={{ transform: this.state.squares1to6 }}
                />
              </Container>
            </div>
          </div>
        </div>
      </>
    );
  }
}

export default Products;
