const initialState = {
  user: {},
  token: '',
  cart: []
}

export default (state = initialState, action) => {
  if (action.type === 'SET_CURRENT_USER') {
    return Object.assign({}, state, { user: action.payload })
  } else if (action.type === 'SET_AUTH_TOKEN') {
    return Object.assign({}, state, { token: action.payload })
  }

  return state
}
