import React from "react";
import { connect } from "react-redux";
import { NavLink } from "react-router-dom";

// reactstrap components
import {
  Col,
  Button,
  Card,
  CardHeader,
  CardBody,
  CardFooter,
  CardImg,
  CardTitle
} from "reactstrap";
import Axios from "axios";

class ViewProduct extends React.Component {
  constructor (props) {
    super(props)
    this.state = {
      product: {}
    }
  }

  async fetchProduct (productId) {
    const response = await Axios.get(`${process.env.REACT_APP_API_URL}/products/${productId}`)
    if (response.status === 200) {
      this.setState({ product: response.data })
    }
  }
  render() {
    return (
      <Col className="col-sm">
        <Card className="card-register">
          <CardHeader>
            <CardImg
              alt="..."
              src={require("assets/img/square-purple-1.png")}
            />
            <CardTitle tag="h6">{this.state.product.name}</CardTitle>
          </CardHeader>
          <CardBody />
          <CardFooter>
            <Button
              className="btn-round"
              color="primary"
              size="sm"
              onClick={_ => {
                this.props.archiveProduct(this.props.id);
              }}
            >
              - Archive
            </Button>
          </CardFooter>
        </Card>
      </Col>
    );
  }
}

const mapStateToProps = state => {
  return { userId: state.user.id };
};

export default connect(mapStateToProps)(ViewProduct);
