import React from "react";
import { connect } from "react-redux";
import { NavLink } from "react-router-dom";

// reactstrap components
import {
  Col,
  Button,
  Row,
  UncontrolledCarousel
} from "reactstrap";

class Product extends React.Component {
  render() {
    return (
      <Row className="justify-content-between" style={{ margin: "4%" }}>
        <Col md="6">
          <Row className="justify-content-between align-items-center">
            <UncontrolledCarousel
              items={[{
                src: this.props.img,
                altText: "product_img",
                caption: this.props.name
              }]}
            />
          </Row>
        </Col>
        <Col md="6">
          <h1 className="profile-title text-left">{this.props.name}</h1>
          <h5 className="text-on-back">{this.props.price}</h5>
          <div className="btn-wrapper pt-3">
            <NavLink
              to={{
                pathname: "/view-product",
                data: { productId: this.props.id }
              }}
            >
              <Button className="btn-simple" color="primary" size="sm">
                <i className="tim-icons icon-book-bookmark" /> Bookmark
              </Button>
            </NavLink>
            <Button
              className="btn-simple"
              color="primary"
              size="sm"
              onClick={_ => {
                this.props.archiveProduct(this.props.id);
              }}
            >
              <i className="tim-icons icon-bulb-63" /> Archive
            </Button>
          </div>
        </Col>
      </Row>
    );
  }
}

const mapStateToProps = state => {
  return { userId: state.user.id };
};

export default connect(mapStateToProps)(Product);
