import React from "react";

// reactstrap components
import {
  Col,
  Card,
  CardHeader,
  CardBody,
  CardFooter,
  CardImg,
  CardTitle,
  Row,
  Container,
  InputGroup,
  InputGroupAddon,
  InputGroupText,
  Input,
  Form,
  Button
} from "reactstrap";

import axios from "axios";

import Navbar from "../../components/Navbars/Navbar";

class EditProduct extends React.Component {
  constructor(props) {
    super(props)
    this.updateProduct = this.updateProduct.bind(this)

    this.state = {
      squares1to6: "",
      squares7and8: "",
      product: {
        name: '',
        price: '',
        is_available: '',
        img_url: '',
        manufacturer_id: '',
        category_id: ''
      },
      manufacturers: [],
      categories: [],
      showAlert: false,
      errorMessage: ''
    };
  }

  async fetchProduct(productId) {
    try {
      const response = await axios.get(
        `${process.env.REACT_APP_API_URL}/products/${productId}`
      );
      console.log(response.data)
      return response.data;
    } catch (e) {
      console.error(e.message);
      return {};
    }
  }

  async componentWillMount () {
    this.setState({
      manufacturers: await this.fetchManufacturers(),
      categories: await this.fetchCategories(),
      product: await this.fetchProduct(this.props.location.data.productId)
    }, _ => {
      this.setFetchedFieldValues()
    })
  }

  componentDidMount () {
    document.body.classList.toggle("register-page");
  }

  componentWillUnmount () {
    document.body.classList.toggle("register-page");
  }

  handleInputChange = (event) => {
    const data = {}
    data[event.target.id] = event.target.value
    const product = this.state.product
    this.setState({ product: Object.assign(product, data) })
  }

  async fetchManufacturers () {
    try {
      const manufacturers = await axios.get(`${process.env.REACT_APP_API_URL}/manufacturers`)
      return manufacturers.data
    } catch (e) {
      console.error(e.message)
    }
    return []
  }

  async fetchCategories () {
    try {
      const categories = await axios.get(`${process.env.REACT_APP_API_URL}/categories`)
      return categories.data
    } catch (e) {
      console.error(e.message)
    }
    return []
  }

  async updateProduct () {
    if (this.inputIsValid()) {
      try {
        const response = await axios.put(`${process.env.REACT_APP_API_URL}/products/${this.state.product.id}`, this.state.product)
        if (response.status === 201) this.props.history.push('/admin-products')
        else this.toggleErrorAlert('An internal server error occured.')
      } catch (e) {
        console.error(e.message)
        this.toggleErrorAlert('There was a problem adding the product.')
      }
    } else this.toggleErrorAlert('Invalid form submission.')
  }

  inputIsValid = () => {
    return Object.keys(this.state.product).reduce((acc, key) => acc && this.state.product[key].length > 0, true)
  }

