import React from "react";
// reactstrap components
import {
  Container,
  Row,
  Col,
  Button,
  Card,
  CardHeader,
  CardBody,
  CardFooter,
  CardImg,
  CardTitle
} from "reactstrap";

import axios from 'axios';

// core components
import LoginNavbar from "components/Navbars/LoginNavbar.jsx";
import Footer from "components/Footer/Footer.jsx";

class Products extends React.Component {
  state = {
    squares1to6: "",
    squares7and8: "",
    products: []
  };

  async componentWillMount () {
    this.setState({ products: await this.fetchProducts() })
  }

  componentDidMount () {
    document.body.classList.toggle("register-page");
    document.documentElement.addEventListener("mousemove", this.followCursor);
  }

  componentWillUnmount () {
    document.body.classList.toggle("register-page");
    document.documentElement.removeEventListener(
      "mousemove",
      this.followCursor
    );
  }

  async fetchProducts () {
    try {
      const products = await axios.get('http://localhost:3001/products')
      return products.data
    } catch (e) {
      console.log(e.message)
    }
    return []
  }

  followCursor = event => {
    let posX = event.clientX - window.innerWidth / 2;
    let posY = event.clientY - window.innerWidth / 6;
    this.setState({
      squares1to6:
        "perspective(500px) rotateY(" +
        posX * 0.05 +
        "deg) rotateX(" +
        posY * -0.05 +
        "deg)",
      squares7and8:
        "perspective(500px) rotateY(" +
        posX * 0.02 +
        "deg) rotateX(" +
        posY * -0.02 +
        "deg)"
    });
  };
  render() {
    return (
      <>
        <LoginNavbar />
        <div className="wrapper">
          <div className="page-header">
            <div className="page-header-image" />
            <div className="content">
              <Container>
                <Row style={{zIndex: 9000}}>
                  {
                    this.state.products.map(product => (
                      <Col key={ product.id } className="col-sm">
                        <Card className="card-register">
                          <CardHeader>
                            <CardImg alt="..." src={require("assets/img/square-purple-1.png")} />
                            <CardTitle tag="h6">{ product.name }</CardTitle>
                          </CardHeader>
                          <CardBody>
                          </CardBody>
                          <CardFooter>
                            <Button className="btn-round" color="primary" size="lg">+ Cart ({ product.price })</Button>
                          </CardFooter>
                        </Card>
                      </Col>
                    ))
                  }
                </Row>
                <div
                  className="square square-3"
                  id="square3"
                  style={{ transform: this.state.squares1to6 }}
                />
                <div
                  className="square square-4"
                  id="square4"
                  style={{ transform: this.state.squares1to6 }}
                />
                <div
                  className="square square-6"
                  id="square6"
                  style={{ transform: this.state.squares1to6 }}
                />
              </Container>
            </div>
          </div>
          <Footer />
        </div>
      </>
    );
  }
}

export default Products;
