import React from "react";

// reactstrap components
import {
  Col,
  Button,
  Card,
  CardHeader,
  CardBody,
  CardFooter,
  CardImg,
  CardTitle
} from "reactstrap";
import Axios from "axios";

class ViewProduct extends React.Component {
  constructor (props) {
    super(props)
    this.state = {
      product: {}
    }
  }

  async componentWillMount () {
    this.setState({ product: await this.fetchProduct(this.props.location.productId) })
  }

  async fetchProduct (productId) {
    try {
      const response = await Axios.get(`${process.env.REACT_APP_API_URL}/products/${productId}`)
      return response.data
    } catch (e) {
      console.error(e.message)
      return {}
    }
  }
  render() {
    return (
      <Col className="col-sm">
        <Card className="card-register">
          <CardHeader>
            <CardImg
              alt="..."
              src={require("assets/img/square-purple-1.png")}
            />
            <CardTitle tag="h6">{this.state.product.name}</CardTitle>
          </CardHeader>
          <CardBody />
          <CardFooter>
            <Button
              className="btn-round"
              color="primary"
              size="sm"
              onClick={_ => {
                this.props.archiveProduct(this.props.id);
              }}
            >
              - Archive
            </Button>
          </CardFooter>
        </Card>
      </Col>
    );
  }
}

export default ViewProduct;
