import React from "react";
import { connect } from 'react-redux'
import axios from 'axios'

// reactstrap components
import {
  Col,
  Button,
  Card,
  CardHeader,
  CardBody,
  CardFooter,
  CardImg,
  CardTitle
} from "reactstrap";

class Product extends React.Component {
  async addReview (review) {
    try {
      const response = await axios.post(`http://localhost:3001/products/${this.props.key}/reviews`)
    } catch (e) {
      console.error(e.message)
    }
  }

  render() {
    return (
      <Col className="col-sm">
        <Card className="card-register">
          <CardHeader>
            <CardImg alt="..." src={require("assets/img/square-purple-1.png")} />
            <CardTitle tag="h6">{ this.props.name }</CardTitle>
          </CardHeader>
          <CardBody />
          <CardFooter>
            <Button className="btn-round" color="primary" size="sm" onClick={ _ => { this.props.addToCart(this.props.index) }}>
              Add to cart ({ this.props.price })
            </Button>
            <Button className="btn-round" color="primary" size="sm" onClick={ _ => { this.props.addToCart(this.props.index) }}>
              Add review
            </Button>
          </CardFooter>
        </Card>
      </Col>
    );
  }
}

const mapStateToProps = state => {
  return { userId: state.user.id }
}

export default connect(mapStateToProps)(Product);
