import React from "react";
import { connect } from "react-redux";
import { Route, Link } from "react-router-dom";
import axios from "axios";

// reactstrap components
import {
  Col,
  Button,
  Card,
  CardHeader,
  CardBody,
  CardFooter,
  CardImg,
  CardTitle
} from "reactstrap";

import ReviewProduct from "../views/ReviewProduct";

class Product extends React.Component {
  async addReview(review) {
    try {
      const response = await axios.post(
        `http://localhost:3001/products/${this.props.key}/reviews`,
        { product_id: this.props.key, user_id: this.props.userId, body: review }
      );
      if (response.status === 201) {
      }
    } catch (e) {
      console.error(e.message);
    }
  }

  render() {
    return (
      <Col className="col-sm">
        <Card className="card-register">
          <CardHeader>
            <CardImg
              alt="..."
              src={require("assets/img/square-purple-1.png")}
            />
            <CardTitle tag="h6">{this.props.name}</CardTitle>
          </CardHeader>
          <CardBody />
          <CardFooter>
            <Button
              className="btn-round"
              color="primary"
              size="sm"
              onClick={_ => {
                this.props.addToCart(this.props.index);
              }}
            >
              + Cart ({this.props.price})
            </Button>
            <Button
              className="btn-round"
              color="primary"
              size="sm"
              tag={Link}
              to="/"
            >
              (<Route path="/add-review" component={ReviewProduct} />)
              Review
            </Button>
          </CardFooter>
        </Card>
      </Col>
    );
  }
}

const mapStateToProps = state => {
  return { userId: state.user.id };
};

export default connect(mapStateToProps)(Product);
