import { connect } from 'react-redux'
import axios from 'axios'

// reactstrap components
import {
  Col,
  Button,
  Card,
  CardHeader,
  CardBody,
  CardFooter,
  CardImg,
  CardTitle
} from "reactstrap";

class Product extends React.Component {
